create table customer (id bigint not null auto_increment, address varchar(255), city varchar(255), country varchar(255), email varchar(255), first_name varchar(255), last_name varchar(255), phone_number varchar(15), zipcode varchar(10), primary key (id)) engine=InnoDB;
