create table roles_privileges (role_id bigint not null, privilege_id bigint not null) engine=InnoDB;
create table shopping_cart (id bigint not null auto_increment, quantity integer, subtotal_price float, product_id bigint, user_id bigint, primary key (id)) engine=InnoDB;
create table user (id bigint not null auto_increment, email varchar(255), first_name varchar(255), last_name varchar(255), password varchar(255), reg_date date, user_name varchar(25), customer_id bigint, primary key (id)) engine=InnoDB;
